mapper.insert:

			SELECT NVL(MAX(EMO_NO), 0) + 1
			FROM EMOTICON

 		INSERT INTO EMOTICON
 			 (
 			 		EMO_NO
 				 	,EMO_NAME
 					,EMO_PRICE
 					,EMO_DESC
 					,EMO_FIRSTIMG
 					,EMO_INNERIMG
 					,EMO_PATH
 					,EMO_STATE
 			)
 			VALUES(
 					#{emoNo}
 				 	,#{emoName}
 					,#{emoPrice}
 					,#{emoDesc}
 					,#{emoFirstimg}
 					,#{emoInnerimg}
 					,'commonResource/emoticon/'
 					,'D001'
 			)

mapper.update:
UPDATE EMOTICON  
SET EMO_STATE = 'D002' 
WHERE EMO_NO=#{emoNo}

UPDATE EMOTICON  
SET EMO_STATE = 'D001' 
WHERE EMO_NO=#{emoNo}

mapper.select:
SELECT * 
FROM EMOTICON
WHERE EMO_NO=#{emoNo}

