mapper.update:
UPDATE FREE_BOARD 
   SET VIEW_CNT = VIEW_CNT + 1 
 WHERE FBOARD_NO = #{fboardNo}


	     UPDATE FREE_BOARD
   		    SET VIEW_CNT = VIEW_CNT+1
     	  WHERE FBOARD_NO = #{fboardNo}


	  UPDATE FREE_BOARD
   		  	  SET TITLE = #{title},
   		    	CONTENT = #{content} 		    	
        WHERE FBOARD_NO = #{fboardNo}

UPDATE FREE_BOARD 
   SET REPLY_CNT = REPLY_CNT + 1 - #{value} 
 WHERE FBOARD_NO = #{fboardNo}
